<script>
import Indexoption from './Indexoption.vue'
import Search from './Search.vue'
export default {
name:'Header',
data(){
  return{
    showoption:false,
    ifclick:false,
    fillcolor:'#A7A4A4',
  }
},
watch:{
  showoption(){
      document.addEventListener('click',(e)=>{
      if(e.target.className!=='optionlist-index'&& e.target.className!=='indexoption'&&e.target.className!=='option-index-text'){
        this.showoption=false;
        this.fillcolor = '#A7A4A4';
        this.ifclick = false;
        }
        else{
          document.removeEventListener('click', () => {})
        }
    })
  }
},
components:{
  Indexoption,
  Search
},
methods:{
  Showoption(){
    this.showoption = !this.showoption
    this.ifclick = !this.ifclick
  },
  Changecolortoblack(){
    this.fillcolor='#000000'
  },
  Changecolortonone(){
    if(this.ifclick == false){
     this.fillcolor='#A7A4A4'
    }
  else{
      this.fillcolor='#000000'
  }
  },
  Gotologin(){
    this.$router.push('/login')
  }
}
}
</script>

